(9, 'OBJ', 'JAMB', 'YrK13', 'ECO', 4, '<div>Ed =&nbsp;<span style=\"text-decoration: underline;\">&Delta;Qtd</span> . <span style=\"text-decoration: underline;\">P</span>&nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;&Delta;Qtd&nbsp; &nbsp;Q</div>\r\n<div>&nbsp;</div>\r\n<div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<span style=\"text-decoration: underline;\">200</span>&nbsp;x&nbsp;<span style=\"text-decoration: underline;\">120</span>&nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 40&nbsp; &nbsp; &nbsp;300</div>\r\n<div>&nbsp;= 2</div>\r\n</div>');
